$(document).ready(function(){$(".team-select input").change(function(){var e=this.value;this.checked?$(".team"+e).slideDown():$(".team"+e).slideUp()}),$(".js--all-teams").click(function(e){e.preventDefault(),$(".team-select input").each(function(){$(this).prop("checked",!0)}),$(".team").each(function(){$(this).slideUp()})})});